class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int n=grid.size(),m=grid[0].size();
        int t1=0,ans=0;
        if(n%2&&m%2){
            ans+=grid[n/2][m/2];
            grid[n/2][m/2]=0;
        }
        int nonpair1=0,pair1=0,nonpair2=0,pair2=0;
        if(n%2){
            int nonpair=0,pair=0;
            int j=0,k=m-1;
            while(j<k){
                if(grid[n/2][j]!=grid[n/2][k])nonpair++;
                else{
                    if(grid[n/2][j]==1)pair++;
                }
                j++;
                k--;
            }
            // if((pair%2==0)||(nonpair>0))ans+=nonpair;
            // else ans+=2;
            nonpair1=nonpair;
            pair1=pair;
        }
        if(m%2){
            int nonpair=0,pair=0;
            int j=0,k=n-1;
            while(j<k){
                if(grid[j][m/2]!=grid[k][m/2])nonpair++;
                else{
                    if(grid[j][m/2]==1)pair++;
                }
                j++;
                k--;
            }
            // if((pair%2==0)||(nonpair>0))ans+=nonpair;
            // else ans+=2;
            nonpair2=nonpair;
            pair2=pair;
        }
        nonpair1+=nonpair2;
        pair1+=pair2;
        if((pair1%2==0)||(nonpair1>0))ans+=nonpair1;
        else ans+=2;
        for(int i=0;i<n/2;i++){
            for(int j=0;j<m/2;j++){
                int cnt=0;
                cnt+=grid[i][j];
                cnt+=grid[n-1-i][j];
                cnt+=grid[i][m-1-j];
                cnt+=grid[n-1-i][m-1-j];
                cnt=min(4-cnt,cnt);
                ans+=cnt;   
            }
        }
        return ans;

    }
};
