class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int m = grid.size(),n = grid[0].size();
        int ans = 0;
        vector<vector<int>>vis(m,vector<int>(n));
        vector<int>nn(5);
        int num1 = 0;
        int pre = 0;
        for(int i = 0;i < m;i++){
            for(int j  = 0;j < n;j++){
                if(vis[i][j])continue;
                int rj = n-1-j;
                int ri = m-1-i;
                vis[i][j] = vis[ri][j] =  1;
                vis[i][rj] = vis[ri][rj] =  1;
                int k = grid[i][j];
                int p = 1;
                if(ri != i)k += grid[ri][j],p++;
                if(rj != j)k += grid[i][rj],p++;
                if(ri != i && rj != j)k += grid[ri][rj],p++;
                if(p == 1){
                    ans += k;
                }else{
                    ans += min(p-k,k);
                }
                if(p == 2){
                   if(k == 1)
                    pre = 1;
                   if(k == 2)
                    num1 += 2;
                }

            }
        }
        if(num1 % 4 != 0 && pre == 0)return ans + 2;
        return ans;
    }
};
