class Solution {
public:
    static bool cmp(pair<int,int>&a, pair<int,int>&b)
    {
        if (a.first != b.first) {
        return a.first < b.first;
        }
        return a.second < b.second;
    }
    int minFlips(vector<vector<int>>& grid) {
        int m = grid.size();
        int n = grid[0].size();

        if((m == 1) and (n == 1))
        {
            if(grid[0][0] == 0){return 0;}
            return 1;
        }
        int ans = 0;
        if((m%2 == 1) and (n%2 == 1))
        {
            if(grid[m/2][n/2] == 1){
                ans++;
            }
        }
        vector<pair<int,int> >dp;

        int j1 = 0, j2 = n-1;
        while(j1 < j2)
        {
            int i1=0, i2 = m-1;
            while(i1 < i2)
            {
                int c0 = 0, c1 = 0;
                if(grid[i1][j1] == 0){
                    c0++;
                }
                else{c1++;}
                if(grid[i2][j1] == 0){
                    c0++;
                }
                else{c1++;}
                if(grid[i1][j2] == 0){
                    c0++;
                }
                else{c1++;}
                if(grid[i2][j2] == 0){
                    c0++;
                }
                else{c1++;}
                i1++;i2--;
                ans+=min(c0,c1);
            }
            if(i1 == i2){
                int c0 = 0, c1 = 0;
                if(grid[i1][j1] == 0){c0++;}
                else{c1++;}
                if(grid[i1][j2] == 0){c0++;}
                else{c1++;}
                dp.push_back(make_pair(c0,c1));
            }
            j1++;j2--;
        }
        if(j1 == j2)
        {
            int i1 = 0, i2 = m-1;
            while(i1 < i2)
            {
                int c0 = 0, c1 = 0;
                if(grid[i1][j1] == 0){
                    c0++;
                }
                else{c1++;}
                if(grid[i2][j1] == 0){
                    c0++;
                }
                else{c1++;}
                i1++;i2--;
                dp.push_back(make_pair(c0,c1));
            }
        }
        sort(dp.begin(),dp.end(),cmp);
        int sz = dp.size();
        int i=0;
        while((i<sz) and (i+1<sz))
        {
            int t1 = dp[i].first + dp[i+1].first;
            int t2 = dp[i].second + dp[i+1].second;
            ans+=min(t1,t2);
            i+=2;
        }
        if(i<sz){
            ans+=(dp[i].second);
        }
        return ans;
    }
};
