/**
 * @param {number[][]} grid
 * @return {number}
 */
var minFlips = function(grid) {
    const rows = grid.length;
    const cols = grid[0].length;
    let flips = 0;

    // Check the middle cell for odd dimension grids
    if (rows % 2 === 1 && cols % 2 === 1) {
        if (grid[Math.floor(rows / 2)][Math.floor(cols / 2)] === 1) {
            flips += 1;
        }
    }

    // Array to count shifts based on sums of pairs
    const shift = [0, 0, 0];

    // Function to update the shift array based on the sum of pairs
    function updateShift(x, y) {
        const sum = x + y;
        shift[sum]++;
    }

    // Handle horizontal middle row for odd row count
    if (rows % 2 === 1) {
        let left = 0, right = cols - 1;
        while (left < right) {
            updateShift(grid[Math.floor(rows / 2)][left], grid[Math.floor(rows / 2)][right]);
            left++;
            right--;
        }
    }

    // Handle vertical middle column for odd column count
    if (cols % 2 === 1) {
        let top = 0, bottom = rows - 1;
        while (top < bottom) {
            updateShift(grid[top][Math.floor(cols / 2)], grid[bottom][Math.floor(cols / 2)]);
            top++;
            bottom--;
        }
    }

    // Calculate flips based on the shift counts
    if (shift[2] % 2 === 1) {
        if (shift[1] <= 0) {
            flips += 2;
        } else {
            flips += shift[1];
        }
    } else {
        flips += shift[1];
    }

    // Handle the rest of the grid
    for (let i = 0; i < Math.floor(rows / 2); ++i) {
        for (let j = 0; j < Math.floor(cols / 2); ++j) {
            const a = grid[i][j];
            const b = grid[rows - 1 - i][j];
            const c = grid[i][cols - 1 - j];
            const d = grid[rows - 1 - i][cols - 1 - j];

            const sum = a + b + c + d;

            if (sum !== 0 && sum !== 4) {
                flips += Math.min(sum, 4 - sum);
            }
        }
    }

    return flips;
};
