class Solution {
public:
    int minFlips(vector<vector<int>>& g) {
        int n=g.size(),m=g[0].size(),r=0,c=0,ans=0;
        for(int i=0;i<n/2;i++){
            for(int j=0;j<m/2;j++){
                int z=0,o=0;
                if(g[i][j]==0)z++;
                else o++;
                if(g[n-i-1][j]==0)z++;
                else o++;
                if(g[i][m-j-1]==0)z++;
                else o++;
                if(g[n-i-1][m-j-1]==0)z++;
                else o++;
                ans+=min(z,o);
            }
        }
        if(n%2==0&&m%2==0)return ans;
        int zo=0,oo=0,zz=0;
        // cout<<ans<<endl;
        if(n%2){
            for(int j=0;j<m/2;j++){
                int i=n/2;
                if(g[i][j]==0&&g[i][m-j-1]==0)zz++;
                else if(g[i][j]==1&&g[i][m-j-1]==1)oo++;
                else zo++;
            }
            // cout<<zo<<endl;
        }
        if(m%2){
            for(int i=0;i<n/2;i++){
                int j=m/2;
                if(g[i][j]==0&&g[n-i-1][j]==0)zz++;
                else if(g[i][j]==1&&g[n-i-1][j]==1)oo++;
                else{
                    zo++;
                    // cout<<i<<" "<<j<<endl;
                    // cout<<g[i][j]<<" "<<g[n-i-1][j]<<endl;
                }
            }
            // cout<<zo<<endl;
        }
        // cout<<zo<<endl;
        if((oo*2)%4==0||zo)ans+=zo;
        else ans+=2;
        if(n%2&&m%2){
            if(g[n/2][m/2]==1)ans++;
        }
        return ans;
    }
};
