class Solution {
    #define pii pair<int,int>
    #define ff first
    #define ss second
public:

    int minFlips(vector<vector<int>>& grid) {

        int rc = grid.size();
        int cc = grid[0].size();
        set<pii> st;
        int ans = 0,cnt1=0,cnt2=0;
        for(int i=0;i<rc;i++)
        {
            for(int j=0;j<cc;j++)
            {
                if(st.find({i,j})!=st.end())
                    continue;
                int z=0,o=0;
                if((i!=rc-i-1) && (j!=cc-j-1)) // 4 points
                {
                    pii p1,p2,p3,p4;
                    p1 = {i,j};
                    p2 = {i,cc-j-1};
                    p3 = {rc-i-1,j};
                    p4 = {rc-i-1,cc-j-1};
                    st.insert({i,j});
                    st.insert({i,cc-j-1});
                    st.insert({rc-i-1,j});
                    st.insert({rc-i-1,cc-j-1});

                    if(grid[p1.ff][p1.ss]==0)
                        z++;
                    else
                        o++;

                    if(grid[p2.ff][p2.ss]==0)
                        z++;
                    else
                        o++;

                    if(grid[p3.ff][p3.ss]==0)
                        z++;
                    else
                        o++;

                    if(grid[p4.ff][p4.ss]==0)
                        z++;
                    else
                        o++;
                    // cout << i << " " << j << endl;
                    // cout << i << " " << cc-j-1 << endl;
                    // cout << rc-i-1 << " " << j << endl;
                    // cout << rc-i-1 << " " << cc-j-1 << endl;
                    // cout << "o " << o << "  z " << z << endl << endl;
                    ans += 4 - max(o,z);
                }
                else if((i!=rc-i-1) || (j!=cc-j-1)) // 2 points
                {
                    pii p1,p2;
                    if(i==rc-i-1)// same row
                    {
                        // cout << "no" << endl;
                        p1 = {i,j};
                        p2 = {i,cc-j-1};
                        st.insert({i,j});
                        st.insert({i,cc-j-1});

                        if(grid[p1.ff][p1.ss]==0)
                            z++;
                        else
                            o++;

                        if(grid[p2.ff][p2.ss]==0)
                            z++;
                        else
                            o++;
                        if(o==2)
                            cnt1++;
                        else if(o==z)
                            cnt2++;
                        ans += 2 - max(o,z);
                    }
                    else   // same col
                    {
                        // cout << "yes" << endl;
                        p1 = {i,j};
                        p2 = {rc-i-1,j};
                        st.insert({i,j});
                        st.insert({rc-i-1,j});
                        if(grid[p1.ff][p1.ss]==0)
                            z++;
                        else
                            o++;

                        if(grid[p2.ff][p2.ss]==0)
                            z++;
                        else
                            o++;
                        if(o==2)
                            cnt1++;
                        else if(o==z)
                            cnt2++;
                        ans += 2 - max(o,z);
                    }
                }
                else // 1 point
                {
                    pii p = {i,j};
                    if(grid[p.ff][p.ss]==1)
                        ans++;
                }
            }
        }
        // cout << "cnt " << cnt << endl;
        if(cnt1%2 && cnt2==0)
            ans+=2;
        return(ans);

    }
};
