#include <vector>
#include <algorithm>

using namespace std;

class Solution {
public:
    int minFlips(vector<vector<int>>& g) {
        int r = g.size();
        int c = g[0].size();
        int f = 0;

        if (r % 2 == 1 && c % 2 == 1) {
            if (g[r / 2][c / 2] == 1) {
                f += 1;
            }
        }

        vector<int> cnt(3, 0);
        if (r % 2 == 1) {
            int l = 0, r2 = c - 1;
            while (l < r2) {
                int s = g[r / 2][l] + g[r / 2][r2];
                cnt[s] += 1;
                l += 1;
                r2 -= 1;
            }
        }

        if (c % 2 == 1) {
            int t = 0, b = r - 1;
            while (t < b) {
                int s = g[t][c / 2] + g[b][c / 2];
                cnt[s] += 1;
                t += 1;
                b -= 1;
            }
        }

        if (cnt[2] % 2 == 1) {
            if (cnt[1] > 0) {
                f += cnt[1];
            } else {
                f += 2;
            }
        } else {
            f += cnt[1];
        }

        for (int i = 0; i < r / 2; ++i) {
            for (int j = 0; j < c / 2; ++j) {
                int a = g[i][j];
                int b = g[r - 1 - i][j];
                int c1 = g[i][c - 1 - j];
                int d = g[r - 1 - i][c - 1 - j];

                int t = a + b + c1 + d;

                if (t != 0 && t != 4) {
                    f += min(t, 4 - t);
                }
            }
        }

        return f;
    }
};
