class Solution {
    public int minFlips(int[][] matrix) {
     int n = matrix.length;
        int m = matrix[0].length;
        int res = 0;
        for (int i = 0; i < n / 2; i++) {
            for (int j = 0; j < m / 2; j++) {
                int[] elements = {
                    matrix[i][j],
                    matrix[n - i - 1][j],
                    matrix[i][m - j - 1],
                    matrix[n - i - 1][m - j - 1]
                };

                int ones = 0;
                for (int e : elements) {
                    if (e == 1) {
                        ones++;
                    }
                }
                res += Math.min(ones, 4 - ones);
            }
        }
        int x = 0;
        int count = 0;
        if (n % 2 != 0) {
            for (int j = 0; j < m / 2; j++) {
                if (matrix[n / 2][j] != matrix[n / 2][m - j - 1]) {
                    res++;
                    x++;
                } else {
                    if (matrix[n / 2][j] == 1) {
                        count++;
                    }
                }
            }
        }
        if (m % 2 != 0) {
            for (int i = 0; i < n / 2; i++) {
                if (matrix[i][m / 2] != matrix[n - i - 1][m / 2]) {
                    res++;
                    x++;
                } else {
                    if (matrix[i][m / 2] == 1) {
                        count++;
                    }
                }
            }
        }
        if (n % 2 != 0 && m % 2 != 0 && matrix[n / 2][m / 2] == 1) {
            res++;
        }
        if (x == 0 && count % 2 != 0) {
            res += 2;
        }
        return res;  
    }
}
