class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int n = grid.size();
        int m = grid[0].size();
        int ans = 0;

        // Function to calculate flips needed for 2x2 sub-grids
        auto countFlips = [&](int i, int j) {
            vector<int> elements = {
                grid[i][j],
                grid[n - i - 1][j],
                grid[i][m - j - 1],
                grid[n - i - 1][m - j - 1]
            };
            int ones = accumulate(elements.begin(), elements.end(), 0);

            return min(ones, 4 - ones);
        };

        // Loop through the 2x2 sub-grids
        for (int i = 0; i < n / 2; ++i) {
            for (int j = 0; j < m / 2; ++j) {
                ans += countFlips(i, j);
            }
        }

        int possible = 0;
        int count = 0;

        // Handle the middle row if n is odd
        if (n % 2 != 0) {
            int midRow = n / 2;
            for (int j = 0; j < m / 2; ++j) {
                if (grid[midRow][j] != grid[midRow][m - j - 1]) {
                    ++ans;
                    ++possible;
                } else if (grid[midRow][j] == 1) {
                    ++count;
                }
            }
        }

        // Handle the middle column if m is odd
        if (m % 2 != 0) {
            int midCol = m / 2;
            for (int i = 0; i < n / 2; ++i) {
                if (grid[i][midCol] != grid[n - i - 1][midCol]) {
                    ++ans;
                    ++possible;
                } else if (grid[i][midCol] == 1) {
                    ++count;
                }
            }
        }

        // Handle the central element if both n and m are odd
        if (n % 2 != 0 && m % 2 != 0 && grid[n / 2][m / 2] == 1) {
            ++ans;
        }

        // Adjust for odd count of 1's in the middle row/column
        if (possible == 0 && count % 2 != 0) {
            ans += 2;
        }

        return ans;
    }
};
