class Solution
{
public:
    int minFlips(vector<vector<int>> &v)
    {
        int n = v.size();
        int m = v[0].size();
        int res = 0;

        auto Flipcnt = [&](int r, int c)
        {
            vector<int> elems = {
                v[r][c],
                v[n - r - 1][c],
                v[r][m - c - 1],
                v[n - r - 1][m - c - 1]};
            int ones = accumulate(elems.begin(), elems.end(), 0);
            return min(ones, 4 - ones);
        };

        for (int r = 0; r < n / 2; r++)
        {
            for (int c = 0; c < m / 2; c++)
                res += Flipcnt(r, c);
        }

        int yes = 0;
        int ones = 0;

        if (n % 2 != 0)
        {
            int midR = n / 2;
            for (int c = 0; c < m / 2; c++)
            {
                if (v[midR][c] != v[midR][m - c - 1])
                {
                    res++;
                    yes++;
                }
                else if (v[midR][c] == 1)
                    ones++;
            }
        }

        if (m % 2 != 0)
        {
            int midC = m / 2;
            for (int r = 0; r < n / 2; r++)
            {
                if (v[r][midC] != v[n - r - 1][midC])
                {
                    res++;
                    yes++;
                }
                else if (v[r][midC] == 1)
                    ones++;
            }
        }

        if (n % 2 != 0 && m % 2 != 0 && v[n / 2][m / 2] == 1)
            res++;
        if (yes == 0 && ones % 2 != 0)
            res += 2;
        return res;
    }
};
