class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int n=grid.size();
        int m=grid[0].size();
        int i=(n+1)/2,j=(m+1)/2;
        int ans=0;
        while(i<n&&j<m)
        {
            int z=0,o=0;
            if(grid[i][j]==0) z++; else o++;
            if(grid[i][m-j-1]==0) z++; else o++;
            if(grid[n-i-1][j]==0) z++; else o++;
            if(grid[n-i-1][m-j-1]==0) z++; else o++;
            j++;
            if(j==m)
            {
                i++;
                j=(m+1)/2;
            }
            ans+=min(z,o);
        }
        int one=0,one2=0,cnt=0,cnt2=0,f1=0,f2=0;
        if(n%2==1)
        {
            for(int i=0;i<(m+1)/2;i++)
            {
                if(grid[n/2][i]!=grid[n/2][m-i-1])
                {
                    cnt++;
                }
                else
                {
                    if(grid[n/2][i]==1)
                    {
                        if(i==m-i-1)
                        f1++;
                        else
                        one+=2;
                    }
                }
            }
        }
        if(m%2==1)
        {
            for(int i=0;i<(n+1)/2;i++)
            {
                if(grid[i][m/2]!=grid[n-i-1][m/2])
                {
                    cnt2++;
                }
                else
                {
                    if(grid[i][m/2]==1)
                    {
                        if(i==n-i-1)
                        f2++;
                        else
                        one2+=2;
                    }
                }
            }
        }

        int tot=one+one2+max(f1,f2);
        tot%=4;
        if(tot%2==1)
        ans++,tot-=1;
        cnt+=cnt2;

        if(tot==0)
        return ans+cnt;
        else
        {
            if(cnt>0)
            return ans+cnt;
            else
            return ans+2;
        }










    }
};
