class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
       int n=grid.size();
       int m=grid[0].size();
       int ans=0;
       // vector<vector<int>>ans(n,vector<int>(m,INT_MAX));
        for(int b=0;b<n/2;b++){
            for(int d=0;d<m/2;d++){
                int c0=0;
                int c1=0;
              if(grid[b][d]==0)
                c0++;
              else
                c1++;
              if(grid[n-1-b][d]==0)
                c0++;
              else
                c1++;
              if(grid[b][m-1-d]==0)
                c0++;
              else
                c1++;
              if(grid[n-1-b][m-1-d]==0)
                c0++;
              else
                c1++;  

              ans+=min(c0,c1);  

            }
        }
        int ans1=0;
        int ct0=0,ct1=0;
       if(n%2){
        for(int b=0;b<m/2;b++){
            if(grid[n/2][b] != grid[n/2][m-1-b]){
                ans++;
                ans1++;
            }
            else{
                if(grid[n/2][b]==0)ct0+=2;
                else ct1+=2;
            }
        }
       }
       if(m%2){
        for(int b=0;b<n/2;b++){
            if(grid[b][m/2] != grid[n-b-1][m/2]){
                ans++;
                ans1++;
            }
            else{
                if(grid[b][m/2] ==0)ct0+=2;
                else ct1+=2;
            }
        }
       }

       if(n%2 && m%2 && grid[n/2][m/2])
       ans++;

       if(ct1%4==0)return ans;

       if(ans1==0)return ans+2;
       //else if (ans1==1) return ans+1;
       return ans;





    }
};
