/**
 * @param {number[][]} grid
 * @return {number}
 */
var minFlips = function(grid) {
    const n = grid.length, m = grid[0].length;
    let res = 0, countMap = new Map();

    if (n % 2 === 1 && m % 2 === 1) {
        if (grid[Math.floor(n / 2)][Math.floor(m / 2)] === 1) {
            res += 1;
        }
    }

    if (n % 2 === 1) {
        for (let j = 0; j < Math.floor(m / 2); j++) {
            let sm = grid[Math.floor(n / 2)][j] + grid[Math.floor(n / 2)][m - 1 - j];
            countMap.set(`middle_row_${sm}`, (countMap.get(`middle_row_${sm}`) || 0) + 1);
        }
    }
    if (m % 2 === 1) {
        for (let i = 0; i < Math.floor(n / 2); i++) {
            let sm = grid[i][Math.floor(m / 2)] + grid[n - 1 - i][Math.floor(m / 2)];
            countMap.set(`middle_col_${sm}`, (countMap.get(`middle_col_${sm}`) || 0) + 1);
        }
    }

    let middleRowTwos = countMap.get('middle_row_2') || 0;
    let middleColTwos = countMap.get('middle_col_2') || 0;
    let middleOnes = (countMap.get('middle_row_1') || 0) + (countMap.get('middle_col_1') || 0);

    if ((middleRowTwos + middleColTwos) % 2 === 1) {
        if (middleOnes > 0) {
            res += middleOnes;
        } else {
            res += 2;
        }
    } else {
        res += middleOnes;
    }


    for (let i = 0; i < Math.floor(n / 2); ++i) {
        for (let j = 0; j < Math.floor(m / 2); ++j) {
            let sm = grid[i][j] + grid[n - 1 - i][j] + grid[i][m - 1 - j] + grid[n - 1 - i][m - 1 - j];
            countMap.set(`quad_${sm}`, (countMap.get(`quad_${sm}`) || 0) + 1);
        }
    }


    for (let i = 1; i <= 3; i++) {
        let count = countMap.get(`quad_${i}`) || 0;
        res += Math.min(i, 4 - i) * count;
    }

    return res;
};
