class Solution {
    public int minFlips(int[][] grid) {
        int n = grid.length;
        int m = grid[0].length;
        int ans = 0;

        if (n % 2 == 1 && m % 2 == 1) {
            if (grid[n / 2][m / 2] == 1) {
                ans += 1;
            }
        }

        int[] temp = new int[3];
        if (n % 2 == 1) {
            int first = 0, second = m - 1;
            while (first < second) {
                int summ = grid[n / 2][first] + grid[n / 2][second];
                temp[summ] += 1;
                first += 1;
                second -= 1;
            }
        }

        if (m % 2 == 1) {
            int first = 0, second = n - 1;
            while (first < second) {
                int summ = grid[first][m / 2] + grid[second][m / 2];
                temp[summ] += 1;
                first += 1;
                second -= 1;
            }
        }

        if (temp[2] % 2 == 1) {
            if (temp[1] > 0) {
                ans += temp[1];
            } else {
                ans += 2;
            }
        } else {
            ans += temp[1];
        }

        for (int i = 0; i < n / 2; ++i) {
            for (int j = 0; j < m / 2; ++j) {
                int first = grid[i][j];
                int second = grid[n - 1 - i][j];
                int third = grid[i][m - 1 - j];
                int fourth = grid[n - 1 - i][m - 1 - j];

                int summ = first + second + third + fourth;

                if (summ != 0 && summ != 4) {
                    ans += Math.min(summ, 4 - summ);
                }
            }
        }

        return ans;
    }
}
