class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        const int n = grid.size(), m = grid[0].size();
        int ans = 0;
        vector<int> cnt(4,0);
        for(int r1=0,r2=n-1;r1<=r2;++r1,--r2){
            for(int c1=0,c2=m-1;c1<=c2;++c1,--c2){
               if(r1==r2){
                    if(c1==c2){
                        ans += grid[r1][c1] == 1;
                    } else {
                        cnt[2*(grid[r1][c1]==1) + (grid[r1][c2]==1)]++;
                    }
               } else {
                    if(c1==c2){
                        cnt[2*(grid[r1][c1]==1) + (grid[r2][c1]==1)]++;
                    } else {
                        int c = grid[r1][c1]+grid[r2][c1] + grid[r1][c2] + grid[r2][c2];
                        ans += min(c,4-c);
                    }
               } 
            }
        }
        int x = cnt[1] + cnt[2];
        if(cnt[3]%2==0) return ans + x;
        else if(x) return ans + x;
        else return ans + 2; 
    }
};
