import java.util.*;

class Solution {
    public int minFlips(int[][] grid) {
        int rows = grid.length;
        int cols = grid[0].length;
        int result = 0;

        if (rows % 2 == 1 && cols % 2 == 1) {
            if (grid[rows / 2][cols / 2] == 1) {
                result += 1;
            }
        }

        int[] counts = new int[3];
        if (rows % 2 == 1) {
            int left = 0, right = cols - 1;
            while (left < right) {
                int sum = grid[rows / 2][left] + grid[rows / 2][right];
                counts[sum] += 1;
                left++;
                right--;
            }
        }

        if (cols % 2 == 1) {
            int top = 0, bottom = rows - 1;
            while (top < bottom) {
                int sum = grid[top][cols / 2] + grid[bottom][cols / 2];
                counts[sum] += 1;
                top++;
                bottom--;
            }
        }

        if (counts[2] % 2 == 1) {
            if (counts[1] > 0) {
                result += counts[1];
            } else {
                result += 2;
            }
        } else {
            result += counts[1];
        }

        for (int i = 0; i < rows / 2; i++) {
            for (int j = 0; j < cols / 2; j++) {
                int topLeft = grid[i][j];
                int bottomLeft = grid[rows - 1 - i][j];
                int topRight = grid[i][cols - 1 - j];
                int bottomRight = grid[rows - 1 - i][cols - 1 - j];

                int sum = topLeft + bottomLeft + topRight + bottomRight;

                if (sum != 0 && sum != 4) {
                    result += Math.min(sum, 4 - sum);
                }
            }
        }

        return result;
    }

    public static void main(String[] args) {
        Scanner scanner = new Scanner(System.in);

        int rows = scanner.nextInt();

        int cols = scanner.nextInt();

        int[][] grid = new int[rows][cols];

        for (int i = 0; i < rows; i++) {
            for (int j = 0; j < cols; j++) {
                grid[i][j] = scanner.nextInt();
            }
        }

        Solution solution = new Solution();
        int result = solution.minFlips(grid);
        System.out.println( result);

        scanner.close();
    }
}
