public class Solution {
    public int minFlips(int[][] grid) {
        int rows = grid.length;
        int cols = grid[0].length;
        int totalFlips = 0;

        // If both dimensions are odd, handle the center element
        if (rows % 2 == 1 && cols % 2 == 1) {
            if (grid[rows / 2][cols / 2] == 1) {
                totalFlips += 1;
            }
        }

        // Array to keep track of shifts needed
        int[] shift = new int[3];

        // Handle the middle row if the number of rows is odd
        if (rows % 2 == 1) {
            int left = 0, right = cols - 1;
            while (left < right) {
                int sum = grid[rows / 2][left] + grid[rows / 2][right];
                shift[sum] += 1;
                left += 1;
                right -= 1;
            }
        }

        // Handle the middle column if the number of columns is odd
        if (cols % 2 == 1) {
            int top = 0, bottom = rows - 1;
            while (top < bottom) {
                int sum = grid[top][cols / 2] + grid[bottom][cols / 2];
                shift[sum] += 1;
                top += 1;
                bottom -= 1;
            }
        }

        // Calculate flips based on shifts
        if (shift[2] % 2 == 1) {
            if (shift[1] > 0) {
                totalFlips += shift[1];
            } else {
                totalFlips += 2;
            }
        } else {
            totalFlips += shift[1];
        }

        // Handle the four corners of each quadrant
        for (int i = 0; i < rows / 2; ++i) {
            for (int j = 0; j < cols / 2; ++j) {
                int a = grid[i][j];
                int b = grid[rows - 1 - i][j];
                int c = grid[i][cols - 1 - j];
                int d = grid[rows - 1 - i][cols - 1 - j];

                int sum = a + b + c + d;

                if (sum != 0 && sum != 4) {
                    totalFlips += Math.min(sum, 4 - sum);
                }
            }
        }

        return totalFlips;
    }




}
