class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {

       int ans=0;
       for (int i=0;i<grid.size()/2;i++) {
        for (int j=0;j<grid[0].size()/2;j++) {
            int l=0;
            if (grid[i][j]==1) {
                l++;
            }
            if (grid[grid.size()-1-i][j]==1) {
                l++;
            }
            if (grid[i][grid[0].size()-1-j]==1) {
                l++;
            }
            if (grid[grid.size()-1-i][grid[0].size()-1-j]==1) {
                l++;
            }

            ans+=min(l,4-l);
        }
       }
       cout<<ans<<" ";
       //ans/=4;
       int cnt=0;
       int revert=0;
       if (grid.size()%2==1) {
        for (int i=0;i<grid[0].size()/2;i++) {
             if (grid[grid.size()/2][i]==grid[grid.size()/2][grid[0].size()-1-i]) {
                if (grid[grid.size()/2][i]==1) {
                    cnt++;
                }
             }
             else {
                revert++;
             }
        }
       }

       if (grid[0].size()%2 ==1) {
        for (int i=0;i<grid.size()/2;i++) {
            if (grid[i][grid[0].size()/2]==grid[grid.size()-1-i][grid[0].size()/2]) {
                if (grid[i][grid[0].size()/2]==1) {
                    cnt++;
                }
            }
            else {
                revert++;
            }
        }
       }
      // revert/=2;
       cout<<revert<<" "<<cnt<<" ";
       cnt*=2;
       if (grid.size()%2==1 && grid[0].size()%2==1 && grid[grid.size()/2][grid[0].size()/2]==1) {
        ans++;
       }

       cnt%=4;

       if (cnt==0) {
         ans+=revert;
       }
       else {
        if (revert) {
            ans+=revert;
        }
        else {
            ans+=2;
        }
       }
       return ans;



    }
};
