class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int n=grid.size(),m=grid[0].size(),ans=0,ans1=0,ans2=0;
        for(int i=0;i<n/2+n%2;i++){
            for(int j=0;j<m/2+m%2;j++){
                int z=0,o=0;
                if(grid[i][j]==1)o++;
                else z++;
                if(grid[i][m-j-1]==1)o++;
                else z++;
                if(grid[n-i-1][j]==1)o++;
                else z++;
                if(grid[n-i-1][m-j-1]==1)o++;
                else z++;
                if(i==n-i-1&&j==m-j-1){
                    if(grid[i][j]==1)ans++;
                    continue;
                }
                else if(j==m-j-1){
                    o=o/2;
                    z=z/2;
                    if(grid[i][j]!=grid[n-i-1][j])ans1++;
                    if(grid[i][j]==grid[n-i-1][j]&&grid[i][j]==1)ans2+=2;
                    //cout<<ans2<<" ";
                    continue;
                }
                else if(i==n-i-1){
                    o=o/2;
                    z=z/2;
                    if(grid[i][j]!=grid[i][m-j-1])ans1++;
                    if(grid[i][j]==grid[i][m-j-1]&&grid[i][j]==1)ans2+=2;

                    continue;
                }
                if(o>=z)ans+=z;
                else ans+=o;
            }
        }

        if(ans2%4==2){
            if(ans1%2==1){
                ans++;
                ans1--;
            }
            else if(ans1>0){
                ans+=2;
                ans1-=2;
            }
            else ans+=2;
        }
        return ans+ans1;
    }
};
