class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int n=grid.size(),m=grid[0].size();
        vector<vector<int>> g(n,vector<int>(m,0));
        int a1=0, a2=0,a3=0;

        for(int i=0;i<=(n-1)/2;i++){
            for(int j=0;j<=(m-1)/2;j++){
                if(i!=n/2 && j!=m/2){
                    int o=0,z=0;
                    if(grid[i][j]) o++; else z++;
                    if(grid[i][m-j-1]) o++; else z++;
                    if(grid[n-i-1][j]) o++; else z++;
                    if(grid[n-i-1][m-j-1]) o++; else z++;
                    // cout<<i<<" "<<j<<" "<<o<<" "<<z<<endl;
                    a1+=min(o,z);
                }
                else if(i!=n/2 || j!=m/2){
                    int o=0,z=0;
                    if(grid[i][j]) o++; else z++;
                    if(grid[i][m-j-1]) o++; else z++;
                    if(grid[n-i-1][j]) o++; else z++;
                    if(grid[n-i-1][m-j-1]) o++; else z++;
                    o/=2,z/=2;
                    // cout<<i<<" "<<j<<" "<<o<<" "<<z<<endl;
                    if(o==1) a3++;
                    else if(o==2) a2++;
                }
                else{
                    if(grid[i][j]) a1++;
                }
            }
            cout<<endl<<a1<<endl;
        }
        cout<<a2<<" "<<a3<<" ";
        if(!a3){
            if(a2&1) a1+=2;
        }
        else{
            a1+=a3;
        }
        return a1;
    }
};
