class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int ans = 0;
        int n = grid.size();
        int m = grid[0].size();
        for(int i = 0;i<grid.size()/2;i++){
            for(int j =0;j<grid[0].size()/2;j++){
                int c = 0;
                if(grid[i][j] == 1) c++;
                if(grid[i][m-j-1] == 1) c++;
                if(grid[n-i-1][j] == 1)c++;
                if(grid[n-i-1][m-j-1] == 1) c++;
                if(c == 1 || c == 3) ans++;
                if(c == 2) ans+=2;

            }
        }
        int ones = 0;
        int ans2 = 0;
        if(n%2 == 1){
            for(int i = 0;i<m/2;i++){
                if(grid[n/2][i] != grid[n/2][m-i-1]) ans2++;
                if(grid[n/2][i] == grid[n/2][m-i-1] && grid[n/2][i] == 1 ) ones+=2;
            }
        }
        if(m%2 == 1){
            for(int i = 0;i<n/2;i++){
                if(grid[i][m/2] != grid[n-i-1][m/2]) ans2++;
                if(grid[i][m/2] == grid[n-i-1][m/2] && grid[i][m/2] == 1) ones+=2;
            }
        }
        if(m%2 == 1 && n%2 == 1 && grid[n/2][m/2] == 1) ans++;
        // if(ans2>0 && ones%4 == 2) ans++;
        if(ans2<=0 && ones%4 == 2) ans+=2;
        return ans+ans2;

    }
};
