class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        ios::sync_with_stdio(0);
        cin.tie(0);

        int ans=0,n=grid.size(), m=grid[0].size();
        for (int i=0; i<n/2; i++) {
            for (int j=0; j<m/2; j++) {
                map<int, map<pair<int,int>,int>> mp;
                mp[grid[i][j]][{i,j}]++;
                mp[grid[i][m-j-1]][{i,m-j-1}]++;
                mp[grid[n-i-1][j]][{n-i-1,j}]++;
                mp[grid[n-i-1][m-j-1]][{n-i-1,m-j-1}]++;
                ans += min(mp[1].size(), mp[0].size());
            }
        }

        if (n%2 && m%2){
            if (grid[n/2][m/2]==1) ans++;
            map<pair<int,int>, int> mp;
            for (int i=0; i<n/2; i++) {
                mp[{grid[i][m/2], grid[n-i-1][m/2]}]++; 
            }
            for (int j=0; j<m/2; j++) {
                 mp[{grid[n/2][j], grid[n/2][m-j-1]}]++; 
            }
            int one=0, zero_one=0;
            zero_one += mp[{0,1}] + mp[{1,0}];

            one += mp[{1,1}];
            if (one%2) {
                if (zero_one) {
                    ans++;
                    zero_one--;
                }
                else ans+=2;
            }
            ans += zero_one;
        }  else if (m%2) {
            map<pair<int,int>, int> mp;
            for (int i=0; i<n/2; i++) {
                mp[{grid[i][m/2], grid[n-i-1][m/2]}]++; 
            }
            int one=0, zero_one=0;
            zero_one += mp[{0,1}] + mp[{1,0}];

            one += mp[{1,1}];
            if (one%2) {
                if (zero_one) {
                    ans++;
                    zero_one--;
                }
                else ans+=2;
            }
            ans += zero_one;
        } else if (n%2) {
            map<pair<int,int>, int> mp;
            for (int j=0; j<m/2; j++) {
                mp[{grid[n/2][j], grid[n/2][m-j-1]}]++; 
            }
            int one=0, zero_one=0;
            zero_one += mp[{0,1}] + mp[{1,0}];

            one += mp[{1,1}];
            if (one%2) {
                if (zero_one) {
                    ans++;
                    zero_one--;
                }
                else ans+=2;
            }
            ans += zero_one;
        }

        return ans; 
    }
};
