class Solution {
public:
    void makeflipsCount(vector<int> &a, int rows, int cols, vector<vector<int>> &grid){
        if (rows &1) {
            int left = 0, right = cols - 1;
            while (left < right) {
                int sum = grid[rows / 2][left] + grid[rows / 2][right];
                a[sum] ++;
                left ++;
                right --;
            }
        }

        if (cols &1) {
            int top = 0, bottom = rows - 1;
            while (top < bottom) {
                int sum = grid[top][cols / 2] + grid[bottom][cols / 2];
                a[sum]++;
                top++;
                bottom --;
            }
        }
    }
    void findTotal(vector<vector<int>> &grid, int rows, int cols, int &totalFlips){
        for (int i = 0; i < rows / 2; ++i) {
            for (int j = 0; j < cols / 2; ++j) {
                int topLeft = grid[i][j];
                int bottomLeft = grid[rows - 1 - i][j];
                int topRight = grid[i][cols - 1 - j];
                int bottomRight = grid[rows - 1 - i][cols - 1 - j];

                int sum = topLeft + bottomLeft + topRight + bottomRight;

                if (sum != 0 && sum != 4) {
                    totalFlips += min(sum, 4 - sum);
                }
            }
    }
    }
    int minFlips(vector<vector<int>>& grid) {
        int rows = grid.size();
        int cols = grid[0].size();
        int totalFlips = 0;

        if (rows &1 && cols &1) {
            if (grid[rows / 2][cols / 2] == 1) {
                totalFlips++;
            }
        }

        vector<int> flipsCount(3, 0);

        makeflipsCount(flipsCount, rows, cols, grid);


        if (flipsCount[2]&1) {
            if (flipsCount[1] > 0) {
                totalFlips += flipsCount[1];
            } else {
                totalFlips += 2;
            }
        } else {
            totalFlips += flipsCount[1];
        }

        findTotal(grid, rows, cols, totalFlips);

        return totalFlips;
    }
};
