class Solution {
    public int minFlips(int[][] grid) {
int m = grid.length, n = grid[0].length;
        int ans = 0;
        for (int i = 0; i < m/2; i++) {
            for (int j = 0; j < n/2; j++) {
                int[] cnt = new int[2];
                cnt[grid[i][j]]++;
                cnt[grid[i][n-1-j]]++;
                cnt[grid[m-1-i][j]]++;
                cnt[grid[m-1-i][n-1-j]]++;

                ans+=Math.min(cnt[0] ,cnt[1]);
            }
        }
        int cnt11 = 0, cnt01 = 0;
        if (m%2 == 1) {
            for (int j = 0; j < n/2; j++) {
                if (grid[m/2][j] != grid[m/2][n-1-j]) {
                    cnt01++;
                }else if (grid[m/2][j] == 1) {
                    cnt11++;
                }
            }
        }
        if (n%2 == 1) {
            for (int i = 0; i < m/2; i++) {
                if (grid[i][n/2] != grid[m-1-i][n/2]) {
                    cnt01++;
                } else if(grid[i][n/2] == 1) {
                    cnt11++;
                }
            }
        }
        int mid = 0;
        if (m%2 == 1 && n%2 == 1 && grid[m/2][n/2] == 1) {
            mid++;
        }
        if (cnt11%2 == 1) {
            if (cnt01 >= 1) {
                ans = ans + cnt01 + mid;
            } else {
                ans = ans + mid + 2;
            }
        } else {
            ans = ans + cnt01 + mid;
        }

        return ans;
    }
}
