class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int n = grid.size(), m = grid[0].size();
        int flips = 0;

        if (n % 2 == 1 && m % 2 == 1) {
            if (grid[n / 2][m / 2] == 1) {
                flips += 1;
            }
        }

        vector<int> t(3, 0);
        if (n % 2 == 1) {
            int i = 0, j = m - 1;
            while (i < j) {
                int sum = grid[n / 2][i] + grid[n / 2][j];
                t[sum] += 1;
                i ++;
                j --;
            }
        }

        if (m % 2 == 1) {
            int i = 0, j = n - 1;
            while (i < j) {
                int sum = grid[i][m / 2] + grid[j][m / 2];
                t[sum] += 1;
                i ++;
                j --;
            }
        }

        if (t[2] % 2 == 1) {
            if (t[1] > 0) {
                flips += t[1];
            } else {
                flips += 2;
            }
        } else {
            flips += t[1];
        }

        for (int i = 0; i < n / 2; ++i) {
            for (int j = 0; j < m / 2; ++j) {
                int one = grid[i][j];
                int two = grid[n - 1 - i][j];
                int three = grid[i][m - 1 - j];
                int four = grid[n - 1 - i][m - 1 - j];

                int sum = one + two + three + four;

                if (sum != 0 && sum != 4) {
                    flips += min(sum, 4 - sum);
                }
            }
        }

        return flips;
    }
};
