class Solution:
    def minFlips(self, grid: List[List[int]]) -> int:
        row_half = int(len(grid) / 2)
        column_half = int(len(grid[0]) / 2)
        diff = 0
        steps = 0
        for i in range(row_half):
            for j in range(column_half):
                top_left = grid[i][j]
                bottom_left = grid[len(grid) - i - 1][j]
                top_right = grid[i][len(grid[i]) - j - 1]
                bottom_right = grid[len(grid) - i - 1][len(grid[i]) - j - 1]
                ones = 0
                for element in [top_left, bottom_left, top_right, bottom_right]:
                    if element == 1:
                        ones += 1
                if ones % 4 != 0:
                    steps += min(ones, 4 - ones)
        forget_ones = 0
        forget_steps = 0
        # deal with odd row
        if len(grid) % 2 == 1:
            row_center = int((len(grid) - 1) / 2)
            for j in range(column_half):
                if grid[row_center][j] != grid[row_center][len(grid[row_center]) - j - 1]:
                    forget_steps += 1
                elif grid[row_center][j] == 1:
                    forget_ones += 2
        # deal with odd column
        if len(grid[0]) % 2 == 1:
            column_center = int((len(grid[0]) - 1) / 2)
            for i in range(row_half):
                if grid[i][column_center] != grid[len(grid) - i - 1][column_center]:
                    forget_steps += 1
                elif grid[i][column_center] == 1:
                    forget_ones += 2
        if len(grid) % 2 == 1 and len(grid[0]) % 2 == 1:
            row_center = int((len(grid) - 1) / 2)
            column_center = int((len(grid[0]) - 1) / 2)
            if grid[row_center][column_center] == 1:
                forget_ones += 1
        if forget_ones % 4 >= 2 and forget_steps > 0:
            left_steps = forget_ones % 4 - 2
        elif forget_steps:
            left_steps = min(forget_ones % 4, 4 - forget_ones % 4)
        else:
            left_steps = forget_ones % 4
        steps += forget_steps + left_steps
        return steps
