class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {

        int m=grid.size(),n=grid[0].size();
        if(n==1&&m==1){
            return grid[0][0];
        }
        int ans=0;
        for(int i=0;i<m/2;i++){
            for(int j=0;j<n/2;j++){
                if(grid[i][j]!=grid[m-i-1][j]||grid[i][j]!=grid[i][n-j-1]||grid[i][j]!=grid[m-i-1][n-j-1]){
                    int x=grid[i][j]+grid[m-i-1][j]+grid[i][n-j-1]+grid[m-i-1][n-j-1];
                    ans+=min(x,4-x);
                }
            }
        }
        // cout<<ans<<endl;
        if(n%2&&m%2&&grid[m/2][n/2]){
            ans++;
        }
        int cnt=0,x=0;
        if(m%2){
            for(int i=0;i<n/2;i++){
                if(grid[m/2][i]!=grid[m/2][n-i-1]){
                    cnt++;
                }else if(grid[m/2][i]==1){
                    x+=2;
                }
            }
        }
        if(n%2){
            // cout<<n<<"hi";
            for(int i=0;i<m/2;i++){
                if(grid[i][n/2]!=grid[m-i-1][n/2]){
                    cnt++;
                }else if(grid[i][n/2]==1){
                    x+=2;
                }
            }
        }
        // cout<<ans<<" "<<cnt<<endl;
        if(cnt==0 && x%4!=0){
            return ans+2;
        }
        ans+=cnt;
        return ans;
    }
};
