class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int cost = 0;
        int ones = 0;
        for(int row = 0; row < grid.size() / 2; row ++)
        {
            for(int col = 0; col < grid[0].size() / 2; col ++)
            {
                int vc = grid[row][col] + grid[grid.size() - 1 - row][col] + grid[row][grid[0].size() - 1 - col] + grid[grid.size() - 1 - row][grid[0].size() - 1 - col];
                if(vc == 0 || vc == 4) continue;
                if(vc <= 2)
                {
                    cost += vc;
                }
                else
                {
                    cost += 4 - vc;
                    ones += 4;
                }
            }
        }
        int buffer = 0;
        if(grid.size() % 2)
        {
            for(int col = 0; col < grid[0].size() / 2; col ++)
            {
                if(grid[grid.size() / 2][col] != grid[grid.size() / 2][grid[0].size() - 1 - col])
                {
                    cost ++;
                    buffer += 2;
                }
                else if(grid[grid.size() / 2][col]) ones += 2;
            }
            if(grid[0].size() % 2 && grid[grid.size() / 2][grid[0].size() / 2]) cost ++;
        }
        if(grid[0].size() % 2)
        {
            for(int row = 0; row < grid.size() / 2; row ++)
            {
                if(grid[row][grid[0].size() / 2] != grid[grid.size() - 1 - row][grid[0].size() / 2])
                {
                    cost ++;
                    buffer += 2;
                }
                else if(grid[row][grid[0].size() / 2]) ones += 2;
            }
        }
        if(ones % 4 == 0 || buffer > 0) return cost;
        return cost + 2;
    }
};
