class Solution {
public:
    int f(vector<int> a,vector<int> b){
        int ans = 0;
        int i =0, j = a.size()-1;
        while(i<j){
            if(a[i]!=a[j]){
                ++ans;
            }
            i++;
            j--;
        }
        return ans;
    }
    int minFlips(vector<vector<int>>& grid) {
        int n = grid.size(), m  = grid[0].size();
        ll ans = 0;
        if(n&1 && m&1){
            if(grid[n/2][m/2]==1) ++ans;
        }
        vector<int> a(3,0);
        if(n&1){
        int i  = 0, j = m-1;
        while(i<j){
            ll x = grid[n/2][i]+ grid[n/2][j];
            a[x]++;
            i++;
            j--;
        }
        }
        if(m&1){
            int i  = 0, j = n-1;
            while(i<j){
            ll x = grid[i][m/2]+ grid[j][m/2];
            a[x]++;
            i++;
            j--;
         }
        }
        if(a[2]&1){
            if(a[1]>0){
                ans+= a[1];
            }else ans+=2;
        }else ans+= a[1];
        fr(i,0,n/2){
            fr(j,0,m/2){
                ll x = grid[i][j],y = grid[n - 1 - i][j],z = grid[i][m - 1 - j],w = grid[n - 1 - i][m - 1 - j];
                ll r = x+y+z+w;
                if (r != 0 && r!=4) {
                    ans += min(r, (ll)(4 - r));
                }

            }
        }
        return ans;
    }
};
