class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int n = grid.size(), m = grid[0].size();
        int ans = 0, n0, n1;
        for (int i = 0; i<n/2; i++) {
            for (int j = 0; j<m/2; j++) {
                n0 = 0;
                n1 = 0;
                if (grid[i][j] == 0){
                    n0++;
                }
                else {
                    n1++;
                }
                if (grid[i][m-1-j] == 0){
                    n0++;
                }
                else {
                    n1++;
                }
                if (grid[n-1-i][j] == 0){
                    n0++;
                }
                else {
                    n1++;
                }
                if (grid[n-1-i][m-1-j] == 0){
                    n0++;
                }
                else {
                    n1++;
                }
                ans += min(n0, n1);
            }
        }
        int x = 0, y = 0;
        if (n%2 == 1) {
            for (int j = 0; j<m/2; j++) {
                if (grid[n/2][j] != grid[n/2][m-1-j]) {
                    x++;
                }
            }
        }
        if (m%2 == 1) {
            for (int i = 0; i<n/2; i++) {
                if (grid[i][m/2] != grid[n-1-i][m/2]) {
                    y++;
                }
            }
        }
        ans += (x+y);
        if (x + y == 0) {
            if (n%2 == 1) {
                for (int j = 0; j<m/2; j++) {
                    if (grid[n/2][j] == 1) {
                        x += 2;
                    }
                }
            }
            if (m%2 == 1) {
                for (int i = 0; i<n/2; i++) {
                    if (grid[i][m/2] == 1) {
                        y += 2;
                    }
                }
            }
            if ((x+y)%4 != 0) {
                ans += 2;
            } 
        }
        if (n%2 ==1 && m%2 == 1 && grid[n/2][m/2]==1) ans++;
        return ans;
    }
};
