typedef long long ll;
#define repn(i, n, e) for(ll i = n; i < e; i++)
#define rep(i, n, e) for(ll i = n; i <= e; i++)
#define sor(a) sort(a.begin(), a.end())
#define as(a) a.begin(), a.end()
#define pb push_back
#define MAP unordered_map<int, int>
#define S second
#define vec(a) vector<int> a
#define mod 1000000007
class Solution {
public:
   int minFlips(vector<vector<int>>& g) {
        int n = g.size();
        int m = g[0].size();
        int ans = 0;
       repn(i,0,n/2) {
           repn(j,0,m/2) {
                vec(elements) = {g[i][j],g[n - i - 1][j],g[i][m - j - 1],g[n - i - 1][m - j - 1]};
                int o = count(elements.begin(), elements.end(), 1);
                ans += min(o, 4 - o);
            }
        }
        int p = 0;
        int c = 0;
        if (n % 2 != 0) {
            repn(j,0,m/2) {
                if (g[n / 2][j] != g[n / 2][m - j - 1]) {
                    ++ans;
                    ++p;
                } else {
                    if (g[n / 2][j] == 1) {
                        ++c;
                    }
                }
            }
        }
                if (m % 2 != 0) {
            repn(i,0,n/2) {
                if (g[i][m / 2] != g[n - i - 1][m / 2]) {
                    ++ans;
                    ++p;
                } else {
                    if (g[i][m / 2] == 1) {
                        ++c;
                    }
                }
            }
        }
        if (n % 2 != 0 && m % 2 != 0 && g[n / 2][m / 2] == 1) {
            ++ans;
        }
        if (p == 0 && c % 2 != 0) {
            ans += 2;
        }
        return ans;
    }
};
