class Solution {
    class Pair{
        int first; int second;
        Pair(int f, int s){
            first=f; second=s;
        }
    }
    Pair rowCount(int []grid){
        int n = grid.length;
        int l=0, r=n-1;
        int count=0;
        int oneCount=0;
        while(l<r){
            if(grid[l]!=grid[r]) count++;
            else if(grid[l]==1) oneCount+=2;
            l++;r--;
        }
        return new Pair(oneCount, count);
    }

    int count(int [][]grid, int i, int j, int m, int n){
        int r1c1 = grid[i][j], r1c2 = grid[i][n-j-1], r2c1 = grid[m-i-1][j], r2c2 = grid[m-1-i][n-j-1];
        int oneCount = 0, zeroCount=0;
        if(r1c1==0) oneCount++; else zeroCount++;
        if(r1c2==0) oneCount++; else zeroCount++;
        if(r2c1==0) oneCount++; else zeroCount++;
        if(r2c2==0) oneCount++; else zeroCount++;
        return Math.min(zeroCount, oneCount);
    }

    public int minFlips(int[][] grid) {
        if(grid.length==0) return 0;
        int m = grid.length, n = grid[0].length;
        int rowHalf = m/2, colHalf = n/2;
        int count=0;
        for(int i=0;i<colHalf; i++){
            for(int j=0;j<rowHalf;j++){
                count+=count(grid, j, i, m, n);
            }
        }
        if((m%2==1 && n%2==1) && grid[m/2][n/2]==1) count++;
        Pair p1 = new Pair(0, 0), p2 = new Pair(0, 0);
        if(m%2==1){
            p1=rowCount(grid[m/2]);
        }
        if(n%2==1){
            int []temp = new int[m];
            for(int i=0;i<m;i++) temp[i] = grid[i][n/2];
            p2=rowCount(temp);
        }
        int oneCount = p1.first+p2.first;
        int totalChanges = p1.second+p2.second;
        count+=totalChanges;
//        output.println(totalChanges);
//        output.println(oneCount);
        if(oneCount%4!=0){
            if(totalChanges==0) count+=2;
        }
        return count;
    }
}
