class Solution {
    public int minFlips(int[][] grid) {
        int flips = 0; 
        int n = grid[0].length;
        int m = grid.length;
        int ones = 0;

        for(int i=0; i<m/2; i++){
            for(int j=0; j<n/2; j++){
                ones = 0;
                if(grid[i][n-j-1]==1){
                    ones++;
                }
                if(grid[m-i-1][j]==1){
                    ones++;
                }
                if(grid[i][j]==1){
                    ones++;
                }
                if(grid[m-i-1][n-j-1]==1){
                    ones++;
                }

                flips += Math.min(ones, 4-ones);
            }
        }

        ones = 0;
        int nf = 0;

        if(m%2==1){
            for(int i=0; i<n; i++){
                if(grid[m/2][i]==1 && (n%2==0 || i!=n/2)){
                    ones++;
                }
                if(i<n/2 && grid[m/2][i]!=grid[m/2][n-i-1]){
                    nf++;
                }
            }
        }
        if(n%2==1){
            for(int i=0; i<m; i++){
                if(grid[i][n/2]==1 && (m%2==0 || i!=m/2)){
                    ones++;
                }
                if(i<m/2 && grid[i][n/2]!=grid[m-i-1][n/2]){
                    nf++;
                }
            }
        }
        if(ones%4!=0){
            if(nf>=Math.min(ones%4, 4-(ones%4))){
                flips += nf;
            }else{
                flips += (nf+2);
            }
        }else{
            flips+=nf;
        }

        if(n%2==1 && m%2==1 && grid[m/2][n/2]==1){
            flips++;
        }

        return flips;
    }
}
