class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int numRows = grid.size();
        int numCols = grid[0].size();

        int flipCount = 0;


        for (int row = 0; row < numRows / 2; ++row) {
            for (int col = 0; col < numCols / 2; ++col) {
                vector<int> elements = {
                    grid[row][col],
                    grid[numRows - row - 1][col],
                    grid[row][numCols - col - 1],
                    grid[numRows - row - 1][numCols - col - 1]
                };

                int onesCount = count(elements.begin(), elements.end(), 1);

                flipCount += min(onesCount, 4 - onesCount);
            }
        }

        int possibleFlips = 0;
        int middleOnesCount = 0;

        if (numRows % 2 != 0) {
            for (int col = 0; col < numCols / 2; ++col) {
                if (grid[numRows / 2][col] != grid[numRows / 2][numCols - col - 1]) {
                    flipCount += 1;
                    possibleFlips += 1;
                } else {
                    if (grid[numRows / 2][col] == 1) {
                        middleOnesCount += 1;
                    }
                }
            }
        }


        if (numCols % 2 != 0) {
            for (int row = 0; row < numRows / 2; ++row) {
                if (grid[row][numCols / 2] != grid[numRows - row - 1][numCols / 2]) {
                    flipCount += 1;
                    possibleFlips += 1;
                } else {
                    if (grid[row][numCols / 2] == 1) {
                        middleOnesCount += 1;
                    }
                }
            }
        }


        if (numRows % 2 != 0 && numCols % 2 != 0 && grid[numRows / 2][numCols / 2] == 1) {
            flipCount += 1;
        }

        if (possibleFlips == 0 && middleOnesCount % 2 != 0) {
            flipCount += 2;
        }

        return flipCount;
    }
};
