class Solution
{
public:
    int minFlips(vector<vector<int>> &grid)
    {
        int rows = grid.size(), cols = grid[0].size();
        int ans = 0;

        if (rows % 2 == 1 && cols % 2 == 1)
            if (grid[rows / 2][cols / 2] == 1)
                ans++;

        vector<int> arr(3, 0);

        if (rows % 2 == 1)
        {
            int left = 0, right = cols - 1;
            while (left < right)
            {
                int total = grid[rows / 2][left] + grid[rows / 2][right];
                arr[total]++;
                left++,right--;
            }
        }

        if (cols % 2 == 1)
        {
            int top = 0, bottom = rows - 1;
            while (top < bottom)
            {
                int total = grid[top][cols / 2] + grid[bottom][cols / 2];
                arr[total]++;
                top++,bottom--;
            }
        }

        if (arr[2]&1)
        {
            if (arr[1] > 0)
                ans += arr[1];
            else
                ans += 2;
        }
        else
            ans += arr[1];

        for (int i = 0; i < rows / 2; ++i)
        {
            for (int j = 0; j < cols / 2; ++j)
            {
                int a = grid[i][j];
                int b = grid[rows - 1 - i][j];
                int c = grid[i][cols - 1 - j];
                int d = grid[rows - 1 - i][cols - 1 - j];

                int total = a + b + c + d;
                if (total != 0 && total != 4)
                {
                    ans += min(total, 4 - total);
                }
            }
        }

        return ans;
    }
};
