class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int n = grid.size();
        int m = grid[0].size();
        int ans = 0;

        for (int i = 0; i < n / 2; ++i) {
            for (int j = 0; j < m / 2; ++j) {
                vector<int> vect = {
                    grid[i][j],
                    grid[n- i -1][j],
                    grid[i][m -j - 1],
                    grid[n - i -1][m -j - 1]
                };

                int ones = count(vect.begin(), vect.end(), 1);
                ans += min(ones, 4 - ones);
            }
        }

        int pos = 0;
        int count = 0;

        if (n % 2 != 0) {
            int midRow = n / 2;
            for (int j = 0; j < m / 2; ++j) {
                if (grid[midRow][j] != grid[midRow][m - j - 1]) {
                    ans += 1;
                    pos += 1;
                } else {
                    if (grid[midRow][j] == 1) {
                        count += 1;
                    }
                }
            }
        }

        if (m % 2 != 0) {
            int midCol = m / 2;
            for (int i = 0; i < n / 2; ++i) {
                if (grid[i][midCol] != grid[n - i - 1][midCol]) {
                    ans += 1;
                    pos += 1;
                } else {
                    if (grid[i][midCol] == 1) {
                        count += 1;
                    }
                }
            }
        }

        if (n % 2 != 0 && m % 2 != 0) {
            if (grid[n / 2][m / 2] == 1) {
                ans += 1;
            }
        }
        if (pos == 0 && count % 2 != 0) {
            ans += 2;
        }

        return ans;
    }
};
