class Solution {
public:

    void freq(char ch, int &zeros, int &ones) {
        if(ch == 0) zeros ++;
        else ones ++;
    }

    int minFlips(vector<vector<int>>& grid) {
        int n = grid.size(), m = grid[0].size();
        int ans = 0;
        vector<vector<int>> seen(n, vector<int> (m, 0));
        for(int i = 0; i < n; i ++) {
            for(int j = 0; j < m; j ++) {
                if(seen[i][j]) continue;
                if((n % 2 == 1 && i == n/2) || (m % 2 == 1 && j == m/2)) continue;
                int zeros = 0, ones = 0;
                freq(grid[i][j], zeros, ones); seen[i][j] = 1;
                freq(grid[i][m - j - 1], zeros, ones); seen[i][m - j - 1] = 1;
                freq(grid[n - i - 1][j], zeros, ones); seen[n - i - 1][j] = 1;
                freq(grid[n - i - 1][m - j - 1], zeros, ones); seen[n - i - 1][m - j - 1] = 1;
                ans += min(zeros, ones);
            }
        }
        // cout << ans << endl;
        if(n % 2 == 1 and m % 2 == 1 and grid[n/2][m/2] == 1) ans ++;
        int same_1_col = 0, same_0_col = 0, diff_col = 0;
        int same_1_row = 0, same_0_row = 0, diff_row = 0;
        if(n % 2 == 1) {
            int row = n/2;
            for(int col = 0; col < m/2; col ++) {
                if(grid[row][col] != grid[row][m - col - 1]) diff_row ++;
                else if(grid[row][col] == 1) same_1_row ++;
                else same_0_row ++;
            }
        }
        if(m % 2 == 1) {
            int col = m/2;
            for(int row = 0; row < n/2; row ++) {
                if(grid[row][col] != grid[n - row - 1][col]) diff_col ++;
                else if(grid[row][col] == 1) same_1_col ++;
                else same_0_col ++;
            }
        }
        ans += diff_col; ans += diff_row;
        // cout << ans << endl;
        same_1_col %= 2; same_1_row %= 2;
        // cout << same_1_col << " " << same_0_col << " " << diff_col << endl;
        // cout << same_1_row << " " << same_0_row << " " << diff_row << endl;
        if(same_1_col != same_1_row) {
            if(same_1_col == 1 and diff_col == 0 and diff_row == 0) ans += 2;
            if(same_1_row == 1 and diff_row == 0 and diff_col == 0) ans += 2;
        }
        return ans;
    }
};


