class Solution {
public:
    ll m,n;        
    int minFlips(vector<vector<int>>& g) {        
        m=g.size();
        n=g[0].size();
        ll ans=0;
        REP(i,0,m/2-1){
            REP(j,0,n/2-1){
                ll cnt0=0,cnt1=0;
                if(g[i][j])cnt1++;
                else cnt0++;

                if(g[m-1-i][j])cnt1++;
                else cnt0++;

                if(g[i][n-1-j])cnt1++;
                else cnt0++;

                if(g[m-1-i][n-1-j])cnt1++;
                else cnt0++;
                ans+=min(cnt0,cnt1);
            }
        }
        cout<<ans<<'\n';
        ll cnt1=0,cnt2=0;
        ll tot1=0,tot2=0;
        if(m%2 && n%2){
            if(g[m/2][n/2]){
                ans++;
                g[m/2][n/2]=0;
            }
        }
        if(m%2){            
            REP(j,0,n/2-1){
                if(g[m/2][j]!=g[m/2][n-1-j]){
                    cnt1++;
                    ans++;
                }
            }
            REP(j,0,n-1){
                tot1+=g[m/2][j];
            }
        }
        if(n%2){
            REP(i,0,m/2-1){
                if(g[i][n/2]!=g[m-1-i][n/2]){
                    cnt2++;
                    ans++;
                }
            }
            REP(i,0,m-1){
                tot2+=g[i][n/2];
            }
        }
        ll tot=tot1+tot2- cnt1- cnt2;
        cout<<cnt1<<" "<<tot1<<" "<<cnt2<<" "<<tot2<<'\n';
        if(tot%4==0);
        else{
            if(cnt1+cnt2);
            else{
                ans+=2;
            }
        }
        return ans;
    }
};
