class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int ans = 0;
        int n = grid.size(), m = grid[0].size();

        if (n % 2 == 0 && m % 2 == 0) {
            // Case: Both n and m are even
            for (int i = 0, j = m - 1; i <= j; i++, j--) {
                for (int a = 0, b = n - 1; a <= b; a++, b--) {
                    int sum = grid[a][i] + grid[a][j] + grid[b][i] + grid[b][j];
                    if (sum == 2) {
                        ans += 2;
                    } else if (sum == 1 || sum == 3) {
                        ans++;
                    }
                }
            }
        } else if (n % 2 == 0 && m % 2 != 0) {
            // Case: n is even and m is odd
            int f = 0, s1 = 0;
            for (int i = 0, j = m - 1; i <= j; i++, j--) {
                for (int a = 0, b = n - 1; a <= b; a++, b--) {
                    if (i == j) {
                        if (grid[a][i] != grid[b][j]) {
                            ans++;
                            f = 1;
                        } else {
                            s1 += grid[a][i] + grid[b][i];
                        }
                        continue;
                    }
                    int sum = grid[a][i] + grid[a][j] + grid[b][i] + grid[b][j];
                    if (sum == 2) {
                        ans += 2;
                    } else if (sum == 1 || sum == 3) {
                        ans++;
                    }
                }
                if (i == j) {
                    if (s1 % 4 != 0) {
                        if (f == 0) ans += 2;
                    }
                }
            }
        } else if (n % 2 != 0 && m % 2 == 0) {
            // Case: n is odd and m is even
            int f = 0, s1 = 0;
            for (int i = 0, j = m - 1; i <= j; i++, j--) {
                for (int a = 0, b = n - 1; a <= b; a++, b--) {
                    if (a == b) {
                        if (grid[a][i] != grid[b][j]) {
                            ans++;
                            f = 1;
                        } else {
                            s1 += grid[a][i] + grid[b][i];
                        }
                        continue;
                    }
                    int sum = grid[a][i] + grid[a][j] + grid[b][i] + grid[b][j];
                    if (sum == 2) {
                        ans += 2;
                    } else if (sum == 1 || sum == 3) {
                        ans++;
                    }
                }
                if (i == j) {
                    if (s1 % 4 != 0) {
                        if (f == 0) ans += 2;
                    }
                }
            }
            if (s1 % 4 != 0 && f == 0) ans += 2;
        } else {
            // Case: Both n and m are odd
            int s1 = 0, f = 0;
            for (int i = 0, j = m - 1; i <= j; i++, j--) {
                for (int a = 0, b = n - 1; a <= b; a++, b--) {
                    if (i == j && a == b) {
                        ans += grid[a][i];
                    } else if (i == j) {
                        if (grid[a][i] != grid[b][i]) {
                            ans++;
                            f = 1;
                        }else {
                            s1 += grid[a][i] + grid[b][i];
                        }
                    } else if (a == b) {
                        if (grid[a][i] != grid[a][j]) {
                            ans++;
                            f = 1;
                        }else {
                          s1 +=  grid[a][i] + grid[b][j];
                        }
                    } else {
                        int sum = grid[a][i] + grid[a][j] + grid[b][i] + grid[b][j];
                        if (sum == 2) {
                            ans += 2;
                        } else if (sum == 1 || sum == 3) {
                            ans++;
                        }
                    }
                }

            }
             if(s1 %4 != 0 && f == 0) ans += 2;
        }

        return ans;
    }
};
