int fun(vector<int>& vv){
    int c0 = 0;
    int c1 = 0;
    for(int x: vv){
        if(x==0)
            c0++;
        else
            c1++;
    }
    if(c0==c1)
        return 2;
    else if(c0>c1)
        return 0;
    else
        return 1;
}

class Solution {
public:
    int minFlips(vector<vector<int>>& v) {
        int n = v.size();
        int m = v[0].size();
        vector<vector<int> > vis( n , vector<int> (m, 0));
        int ans = 0;
        vector<int> canDo; 
        for(int i=0;i<n;i++){
            for(int j=0;j<m;j++){
                if(vis[i][j]==1){
                    continue;
                }
                set<pair<int,int>> s;
                s.insert({i,j});
                s.insert({i,m-j-1});
                s.insert({n-i-1,j});
                s.insert({n-i-1,m-j-1});
                vector<int> vv;
                for(auto& it: s){
                    vv.pb(v[it.F][it.S]);
                }
                int res = fun(vv);
                if(res==2){
                    int sz = s.size();
                    ans += sz/2;
                    canDo.pb(sz);
                    for(auto& it: s){
                        vis[it.F][it.S] = 1;
                        v[it.F][it.S] = 2;
                    }
                }
                else{
                    for(auto& it: s){
                        vis[it.F][it.S] = 1;
                        if(v[it.F][it.S]!=res){
                            ans++; 
                            v[it.F][it.S] = res;
                        }
                    }
                }

            }
        }
        // for(int i=0;i<n;i++){
        //     for(int j=0;j<m;j++){
        //         cout << v[i][j] << " ";
        //     }
        //     cout << endl;
        // }
        int c1 = 0;
        int c2 = 0;
        for(int i=0;i<n;i++){
            for(int j=0;j<m;j++){
                if(v[i][j]==1)
                    c1++;
                else if(v[i][j]==2)
                    c2++;
            }
        }
        int over = c1%4;
        for(int x: canDo){
            over = min(over,(over+x)%4);
        }
        return ans + over;
