class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int i,j,n=grid.size(),m=grid[0].size(),ans=0,ct=0,ex=0;

        for(i=0;i<n;i++){
            for(j=0;j<m;j++){
                if(n-i-1<i or m-1-j<j)break;
                int topleft=grid[i][j];
                int topright=grid[i][m-1-j];
                int bottomleft=grid[n-1-i][j];
                int bottomright=grid[n-1-i][m-1-j];
                if(i!=n-1-i and j!=m-1-j){
                    int sum=topleft+topright+bottomleft+bottomright;
                    if(sum<=2)ans+=sum;
                    if(sum==3)ans+=1;
                }
                if(i==n-1-i and j!=m-1-j){
                    int sum=topleft+topright;
                    if(sum==1)ex++;
                    if(sum==2)ct++;
                }
                if(i!=n-1-i and j==m-1-j){
                    int sum=topleft+bottomright;
                    if(sum==1)ex++;
                    if(sum==2)ct++;
                }
                if(i==n-1-i and j==m-1-j){
                    if(topleft==1)ans++;
                }
            }
        }
        cout<<ex<<endl;
        if(ct&1){
            if(ex)return ans+1+ex-1;
            else return ans+2;
        }
        else return ans+ex;

        return ans;

    }
};
