class Solution {
public:

    int minFlips(vector<vector<int>>& grid) {
        int n = grid.size();
        int m = grid[0].size();
        int res = 0;

        for (int i = 0; i < n / 2; ++i) {
            for (int j = 0; j < m / 2; ++j) {
                vector<int> elements = {
                    grid[i][j],
                    grid[n - i - 1][j],
                    grid[i][m - j - 1],
                    grid[n - i - 1][m - j - 1]
                };

                int ones = accumulate(elements.begin(), elements.end(), 0);
                res += min(ones, 4 - ones);
            }
        }

        int pos = 0;
        int count = 0;


        if (n % 2 != 0) {
            int midRow = n / 2;
            for (int j = 0; j < m / 2; ++j) {
                if (grid[midRow][j] != grid[midRow][m - j - 1]) {
                    res += 1;
                    pos += 1;
                } else {
                    if (grid[midRow][j] == 1) {
                        count += 1;
                    }
                }
            }
        }

        if (m % 2 != 0) {
            int mC = m / 2;
            for (int i = 0; i < n / 2; ++i) {
                if (grid[i][mC] != grid[n - i - 1][mC]) {
                    res += 1;
                    pos += 1;
                } else {
                    if (grid[i][mC] == 1) {
                        count += 1;
                    }
                }
            }
        }

        if (n % 2 != 0 && m % 2 != 0 && grid[n / 2][m / 2] == 1) {
            res += 1;
        }

        if (pos == 0 && count % 2 != 0) {
            res += 2;
        }

        return res;
    }
};
