#include<bits/stdc++.h>
using namespace std;

class Solution {
public:
    int minFlips(vector<vector<int>>& a) {
        int n = a.size(), m = a[0].size();
        int Cnt = 0;
        for (int i1 = 0, i2 = n - 1;i1 < i2;i1++, i2--) {
            for (int j1 = 0, j2 = m - 1;j1 < j2;j1++, j2--) {
                //  该四个位置的元素值必须相等.
                int t = a[i1][j1] + a[i1][j2] + a[i2][j1] + a[i2][j2];
                Cnt = Cnt + min(t, 4 - t);
            }
        }
        if (n % 2 == 0 && m % 2 == 0) return Cnt;
        else if (n % 2 == 0) {
            int t1 = 0, t2 = 0;
            int j = m / 2;
            for (int i1 = 0, i2 = n - 1;i1 < i2;i1++, i2--) {
                if (a[i1][j] == a[i2][j] && a[i1][j] == 1) t1++;
                if (a[i1][j] != a[i2][j]) t2++;
            }
            t1 %= 2;
            if (t2) {
                Cnt += t2;
            }
            else {
                if (t1) Cnt += 2;
            }
            return Cnt;
        }
        else if (m % 2 == 0) {
            int t1 = 0, t2 = 0;
            int i = n / 2;
            for (int j1 = 0, j2 = m - 1;j1 < j2;j1++, j2--) {
                if (a[i][j1] == a[i][j2] && a[i][j1]) t1++;
                if (a[i][j1] != a[i][j2]) t2++;
            }
            t1 %= 2;
            if (t2) {
                Cnt += t2;
            }
            else {
                if (t1) Cnt += 2;
            }
            return Cnt;
        }
        else {
            int t1 = 0, t2 = 0;
            int j = m / 2;
            for (int i1 = 0, i2 = n - 1;i1 < i2;i1++, i2--) {
                if (a[i1][j] == a[i2][j] && a[i1][j] == 1) t1++;
                if (a[i1][j] != a[i2][j]) t2++;
            }
            int i = n / 2;
            for (int j1 = 0, j2 = m - 1;j1 < j2;j1++, j2--) {
                if (a[i][j1] == a[i][j2] && a[i][j1]) t1++;
                if (a[i][j1] != a[i][j2]) t2++;
            }
            t1 %= 2;
            if (t2) {
                Cnt += t2;
            }
            else {
                if (t1) Cnt += 2;
            }
            if (a[i][j]) Cnt++;
            return Cnt;
        }
    }
};
