class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int n = grid.size();
        int m = grid[0].size();

        int ans = 0;

        if (n % 2 == 1 && m % 2 == 1) 
        {
            if (grid[n / 2][m / 2] == 1) 
            {
                ans += 1;
            }
        }

        vector<int> v(3, 0);
        if (n % 2 == 1) 
        {
            int a = 0, b = m - 1;
            while (a < b) 
            {
                int t = grid[n / 2][a] + grid[n / 2][b];
                v[t] += 1;
                a += 1;
                b -= 1;
            }
        }

        if (m % 2 == 1) 
        {
            int a = 0, b = n - 1;
            while (a < b) 
            {
                int t = grid[a][m / 2] + grid[b][m / 2];
                v[t] += 1;
                a += 1;
                b -= 1;
            }
        }

        if (v[2] % 2 == 1) 
        {
            if (v[1] > 0) 
            {
                ans += v[1];
            } 
            else 
            {
                ans += 2;
            }
        } 
        else 
        {
            ans += v[1];
        }

        for (int i = 0; i < n / 2; ++i) 
        {
            for (int j = 0; j < m / 2; ++j) 
            {
                int a = grid[i][j];
                int b = grid[n - 1 - i][j];
                int c = grid[i][m - 1 - j];
                int d = grid[n - 1 - i][m - 1 - j];

                int t = a + b + c + d;

                if (t != 0 && t != 4) 
                {
                    ans += min(t, 4 - t);
                }
            }
        }

        return ans;
    }
};
