func minFlips(grid [][]int) int {
    var m, n = len(grid), len(grid[0])
    var hm, hn = m/2, n/2

    var res = 0
    for i := 0; i < hm; i++ {
        for j := 0; j < hn; j++ {
            var total = grid[i][j] + grid[i][n-j-1] + grid[m-i-1][j] + grid[m-i-1][n-j-1]
            if total == 1 || total == 3 {
                res += 1
            } else if total == 2 {
                res += 2
            }
        }
    }
    // fmt.Println(res)

    var mNum = 0
    var mIsOdd = false
    var mChange = 0
    if m % 2 == 1 {
        for j := 0; j < n/2; j++ {
            var total = grid[hm][j]+grid[hm][n-j-1]
            if total == 2 {
                mNum += 2
            } else if total == 1 {
                mIsOdd = true
                mNum += 2
                mChange++
            }
        }
    }
    // fmt.Println(mNum, mIsOdd, mChange)
    var nNum = 0
    var nIsOdd = false
    var nChange = 0
    if n % 2 == 1 {
        for i := 0; i < m/2; i++ {
            var total = grid[i][hn]+grid[m-i-1][hn]
            if total == 2 {
                nNum += 2
            } else if total == 1 {
                nIsOdd = true
                nNum += 2
                nChange++
            }
        }
    }
    // fmt.Println(nNum, nIsOdd, nChange)
    if (mNum + nNum) % 4 == 0 {
        res += mChange
        res += nChange
    } else if m % 2 == 1 && n % 2 == 1{
        if !mIsOdd && !nIsOdd {
            res += mChange + nChange + 2
        } else {
            res += mChange + nChange
        }
    } else if mNum % 4 == 2 {
        res += nChange
        if !mIsOdd {
            res += mChange + 2
        } else {
            res += mChange
        }
    } else if nNum % 4 == 2 {
        res += mChange
        if !nIsOdd {
            res += nChange + 2
        } else {
            res += nChange
        }
    }

    if m % 2 == 1 && n % 2 == 1 && grid[hm][hn] == 1 {
        res += 1
    }
    // fmt.Println("----")
    return res
}
