class Solution {
    public int minFlips(int[][] grid) {
        int ans=0,n=grid.length,m=grid[0].length;
        for(int i=0;i<n/2;i++) {
            for(int j=0;j<m/2;j++) {
                int count=grid[i][j]+grid[i][m-1-j]+grid[n-1-i][j]+grid[n-1-i][m-1-j];
                ans+=Math.min(count,4-count);
            }
        }
        if(n%2==1 && m%2==1 && grid[n/2][m/2]==1)
            ans++;
        int mcount=0,count1=0;
        if(n%2==1) {
            for(int j=0;j<m/2;j++) {
                if(grid[n/2][j]!=grid[n/2][m-1-j])
                    mcount++;
                else if(grid[n/2][j]==1)
                    count1+=2;
            }
        }
        if(m%2==1) {
            for(int i=0;i<n/2;i++) {
                if(grid[i][m/2]!=grid[n-1-i][m/2])
                    mcount++;
                else if(grid[i][m/2]==1)
                    count1+=2;
            }
        }
        if(count1%4==2 && mcount==0)
            mcount+=2;
        return (ans+mcount);
    }
}
