class Solution {
public:
    int minFlips(vector<vector<int>>& matrix) {
        int rows = matrix.size(), cols = matrix[0].size();
        int flipCount = 0;

        if (rows % 2 == 1 && cols % 2 == 1) {
            if (matrix[rows / 2][cols / 2] == 1) {
                flipCount += 1;
            }
        }

        vector<int> balance(3, 0);
        if (rows % 2 == 1) {
            int left = 0, right = cols - 1;
            while (left < right) {
                int sum = matrix[rows / 2][left] + matrix[rows / 2][right];
                balance[sum] += 1;
                left += 1;
                right -= 1;
            }
        }

        if (cols % 2 == 1) {
            int top = 0, bottom = rows - 1;
            while (top < bottom) {
                int sum = matrix[top][cols / 2] + matrix[bottom][cols / 2];
                balance[sum] += 1;
                top += 1;
                bottom -= 1;
            }
        }

        if (balance[2] % 2 == 1) {
            if (balance[1] > 0) {
                flipCount += balance[1];
            } else {
                flipCount += 2;
            }
        } else {
            flipCount += balance[1];
        }

        for (int i = 0; i < rows / 2; ++i) {
            for (int j = 0; j < cols / 2; ++j) {
                int topLeft = matrix[i][j];
                int bottomLeft = matrix[rows - 1 - i][j];
                int topRight = matrix[i][cols - 1 - j];
                int bottomRight = matrix[rows - 1 - i][cols - 1 - j];

                int sum = topLeft + bottomLeft + topRight + bottomRight;

                if (sum != 0 && sum != 4) {
                    flipCount += min(sum, 4 - sum);
                }
            }
        }

        return flipCount;
    }
};
