class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
    int row = grid.size(), col = grid[0].size(); int ans = 0;

    if ((row%2) && (col%2) && (grid[row/2][col/2])){
        ans++;
    }
    vector<int>pc = {0,0,0}; 
    if (row%2) {
        for (int i = 0; i < col/2;i++) {
            pc[grid[row/2][i] + grid[row/2][col-i-1]]++;
        }
    }
    if (col%2) {
        for (int i = 0; i < row/2;i++) {
            pc[grid[i][col/2] + grid[row-i-1][col/2]]++;
        }
    }
    if (pc[2]%2) {
        ans += pc[1]>0?pc[1]:2;
    } else {
        ans += pc[1];
    }

    for (int i = 0; i<row/2;i++) {
        for (int j = 0; j < col/2;j++) {
            int qsum = grid[i][j] + grid[row-1-i][j] + grid[i][col-1-j] + grid[row-1-i][col-1-j];
            if (qsum != 0 && qsum != 4) {
                ans += min(qsum,4-qsum);
            }
        }
    }
    return ans;
    }
};
