class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
         int n =grid.size();
         int m =grid[0].size();
         int res = 0;

        if (n%2 ==1 && m % 2 == 1) {
            if (grid[n/2][m/2]==1)res++;
        }

        vector<int> temp(3, 0);
        if (n % 2 == 1) {
            int left= 0, right =m-1;
            while (left< right) {
                int num = grid[n/2][left] + grid[n / 2][right];
                temp[num]++;
                left++;
                right--;
            }
        }

        if (m %2==1) {
            int left= 0, right=n - 1;
            while (left < right) {
                int num = grid[left][m/ 2]+grid[right][m/ 2];
                temp[num]++;
                left++;
                right--;
            }
        }

        if (temp[2] % 2 == 1) {
            if (temp[1] > 0) res += temp[1];
            else res += 2;

        } else {
            res += temp[1];
        }

        for (int i=0;i<n/2;i++) {
            for (int j=0;j<m/2;j++) {
                int x1 = grid[i][j];
                int x2 = grid[n-1-i][j];
                int x3 = grid[i][m-1-j];
                int x4 = grid[n-1-i][m-1-j];

                int ans=x1+x2+x3+x4;

                if (ans!=0 && ans != 4) {
                    res += min(ans,4-ans);
                }
            }
        }

        return res;

    }
};
