class Solution:
    def minFlips(self, grid: List[List[int]]) -> int:
        g = grid
        rows = len(grid)
        cols = len(grid[0])
        rh = rows // 2
        ch = cols // 2
        r = 0
        for i in range(rh):
            for j in range(ch):
                count = g[i][j] + g[rows-i-1][j] + g[i][cols-j-1] + g[rows-i-1][cols-j-1]
                r += min(count,4-count)

        counter = 0
        r2 = 0
        if rows % 2 :
            for j in range(ch):
                count = g[rh][j] + g[rh][cols-j-1]
                counter += count
                r2 += min(count,2-count)
        if cols % 2 :
            for i in range(rh):
                count = g[i][ch] + g[rows-i-1][ch]
                counter += count
                r2 += min(count,2-count)



        if r2 == 0 and counter % 4 : 
            r2 += 2
        if cols % 2 and rows % 2 and grid[rh][ch]: 
            r += 1


        return r + r2
